package lesonli.cordova.alipay;import java.util.Map;public class Result {	private String resultStatus;	private String result;	private String memo;	public Result(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if ("resultStatus".equals(key)) {				resultStatus = rawResult.get(key);			} else if ("result".equals(key)) {				result = rawResult.get(key);			} else if ("memo".equals(key)) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}